function openCalculator(){document.getElementById("calculatorApp").innerHTML=calculatorHTML;for(var t,e=document.querySelectorAll(".digit"),n=0;n<e.length;n++)e[n].addEventListener("click",function(){var e=this.value;t=document.getElementById("number").innerHTML+=e});document.getElementById("clear").addEventListener("click",function(){document.getElementById("number").innerHTML=""});for(var l=document.querySelectorAll(".percent"),u=0;u<l.length;u++)l[u].addEventListener("click",function(){if(""!==document.getElementById("number").innerHTML){var e=this.value;document.getElementById("number").innerHTML=Math.round(t*e*100)/100}});document.getElementById("closeCalcApp").addEventListener("click",function(){document.getElementById("calculatorApp").innerHTML=""})}var calculatorHTML='<div id="calculatorBackground"></div><div id="calculatorMain"><div id="number"></div><div class="row"> <button class="percent" value=".05">5%</button> <button class="percent" value=".10">10%</button> <button class="percent" value=".15">15%</button> <button class="percent" value=".20">20%</button></div><div class="row keypad"> <button class="digit" value="1">1</button> <button class="digit" value="2">2</button> <button class="digit" value="3">3</button></div><div class="row keypad"> <button class="digit" value="4">4</button> <button class="digit" value="5">5</button> <button class="digit" value="6">6</button></div><div class="row keypad"> <button class="digit" value="7">7</button> <button class="digit" value="8">8</button> <button class="digit" value="9">9</button></div><div class="row keypad"> <button id="closeCalcApp" class="digit">close</button> <button class="digit" value="0">0</button> <button id="clear" class="digit">C</button></div></div>';document.getElementById("calculator").addEventListener("click",openCalculator);